import React from 'react';
import { useState, FormEvent } from 'react';

const Home = () =>{

     interface Hotel {
          hotel: {
            name: string;
            rating: number;
            address: {
              fullAddress: string;
            };
          };
          offers: {
            price: {
              total: number;
              currency: string;
            };
          }[];
     }

     const [place, setPlace] = useState('');
     const [arrivalDate, setArrivalDate] = useState('');
     const [departureDate, setDepartureDate] = useState('');
     const [hotelResults, setHotelResults] = useState<Hotel[]>([]);


     const handleSubmit = async (event: FormEvent) => {
          event.preventDefault();
          const hotels = await fetchHotels(place, arrivalDate, departureDate);
          setHotelResults(hotels);
        };


     const fetchHotels = async (place: String, arrivalDate: String, departureDate: String) => {
          const apiUrl = 'https://api.amadeus.com/v2/shopping/hotel-offers';
          const apiKey = process.env.AMADEUS_APIKEY;

          const response = await fetch(
            `${apiUrl}?cityCode=${place}&checkInDate=${arrivalDate}&checkOutDate=${departureDate}&apikey=${apiKey}`
          );
          const data = await response.json();

          return data.data;
        };

     return(
          <div>
               <h1>Home Page</h1>
               <form onSubmit={handleSubmit}>
                    <br/>
                    <input type="text" placeholder="Place of visit" value={place} onChange={(e) => setPlace(e.target.value)}/>
                    <br/>
                    <br/>
                    <br/>
                    <p>Date of Visit:</p>
                    <input type="date" value={arrivalDate} onChange={(e) => setArrivalDate(e.target.value)}/>
                    <br/>
                    <br/>
                    <br/>
                    <p>Date of Leave:</p>
                    <input type="date" value={departureDate} onChange={(e) => setDepartureDate(e.target.value)}/>
                    <br/>
                    <br/>
                    <button type="submit">Search Hotels</button>
                    <br/>
               </form>
               {hotelResults.map((hotel, index) => (
                    <div key={index}>
                         <h2>{hotel.hotel.name}</h2>
                         <p>Rating: {hotel.hotel.rating}</p>
                         <p>Address: {hotel.hotel.address.fullAddress}</p>
                         <p>Price: {hotel.offers[0].price.total} {hotel.offers[0].price.currency}</p>
                    </div>
               ))}
          </div>
     );
};

export default Home;
